# simply run:
# ruby tools/seat_maps/koh_pich_q_solo_concert_seat_map_generator.rb

# rubocop:disable Layout/LineLength,Metrics/CyclomaticComplexity,Metrics/PerceivedComplexity
class KohPichQSoloConcertSeatMapGenerator
  attr_reader :title, :start_seat, :left_to_right, :row_column_hash, :excludes

  alias left_to_right? left_to_right

  def initialize(title:, start_seat:, left_to_right:, row_column_hash:, excludes: {})
    @title = title
    @start_seat = start_seat
    @left_to_right = left_to_right
    @row_column_hash = row_column_hash
    @excludes = excludes || {}
  end

  def call(log: false)
    # data used for logging & check previous seats length.
    # eg. { :A => ['A52:1', 'A54:2', 'A56:3', 'A58:4'], :B => ['B52:38', 'B54:37', 'B56:36', 'B58:35'] }.
    data = {}

    # physical seat index is resulta can be used to quickly get physical seat via bib/sequence number
    # eg. { 1 => 'O52', 2 => 'O54', 3 => 'O56', 4 => 'O58', 5 => 'O60', 6 => 'O62' }
    physical_seat_index = {}

    row_column_hash.each_with_index do |(seat_label, seats_per_row), row_index|
      previous_seats_count = data.values.flatten.size
      sequence = get_sequence(row_index, seats_per_row, start_from: previous_seats_count, exclude_positions: excludes[seat_label] || [])

      seats_per_row.times do |column_index|
        # koh pich seat is increase/decrease by 2
        physical_seat_position = if left_to_right?
                                   start_seat.odd? ? start_seat - (column_index * 2) : start_seat + (column_index * 2)
                                 else
                                   start_seat.odd? ? start_seat + (column_index * 2) : start_seat - (column_index * 2)
                                 end

        # when it decreasing become negative, start increasing.
        physical_seat_position = -physical_seat_position + 1 if physical_seat_position.negative?
        physical_seat = "#{seat_label}#{physical_seat_position}"

        data[seat_label] ||= []
        data[seat_label] << "#{physical_seat}:#{sequence[column_index] || 'N/A'}"

        physical_seat_index[sequence[column_index]] = physical_seat unless sequence[column_index].nil?
      end

      data[seat_label].reverse! unless left_to_right?
    end

    print_log(data, physical_seat_index) if log
  end

  def print_log(data, physical_seat_index)
    puts "#{title} (Total seat: #{physical_seat_index.length})"
    puts physical_seat_index.sort.to_h.values.join(',')

    puts "\nLayouts:"
    data.each_value { |row| puts row.join(' ') }
    puts '-' * 100
    puts "\n"
  end

  # index.odd: 100, 99, .. 51
  # index.even: 1, 2, 3, .. 50
  def get_sequence(row_index, seats_per_row, start_from: 0, exclude_positions: [])
    sequence_position = 0
    sequence = (1..seats_per_row).map do |position|
      if exclude_positions.include?(position)
        nil
      else
        sequence_position += 1
        start_from + sequence_position
      end
    end

    row_index.odd? ? sequence.reverse : sequence
  end
end

